import React, { useState } from "react";

export default function CounterHooks({ initialCount }) {
  const [state, setState] = useState({ count: initialCount });
  return (
    <div>
      <button
        onClick={() => {
          setState((prevState) => {
            {
              count: prevState.count - 1;
            }
          });
        }}
      >
        -
      </button>
      <span>{state.count}</span>
      <button>+</button>
    </div>
  );
}
