import React, { useState, useEffect } from "react";
import RecipeList from "./RecipeList";
import "../css/App.css";
import RecipeEdit from "./RecipeEdit";

export const RecipeContext = React.createContext();
const LOCAL_STORAGE_KEY = "cookingwithReact.recipes";

function App() {
  const sampleRecipes = [
    {
      name: "Recipe 1",
      id: 1,
      cookTime: "1 hr",
      servings: 3,
      instructions: "Cook that shit",
      ingredients: [
        {
          id: 1,
          name: "Ingredient 1",
          amount: "2 lbs",
        },
      ],
    },
  ];

  const [selectedRecipeId, setSelectedRecipeId] = useState();
  const [search, setSearch] = useState("");
  const [recipes, setRecipes] = useState(sampleRecipes);
  const [shownRecipes, setShownRecipes] = useState(recipes);
  const selectedRecipe = recipes.find(
    (recipe) => recipe.id === selectedRecipeId
  );

  useEffect(() => {
    const recipeJSON = localStorage.getItem(LOCAL_STORAGE_KEY);
    if (recipeJSON != null) {
      setRecipes(JSON.parse(recipeJSON));
      setShownRecipes(recipes);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(recipes));
  }, [recipes]);

  const recipeContextValue = {
    handleRecipeAdd,
    handleRecipeDelete,
    handleRecipeSelect,
    handleRecipeChange,
  };

  const handleSearch = (e) => {
    const searchedRecipe = recipes.filter(
      (recipe) => recipe.name.toLowerCase() === e.target.value.toLowerCase()
    );
    if (searchedRecipe.length !== 0) {
      setShownRecipes(searchedRecipe);
    }
    setShownRecipes(recipes);
  };

  function handleRecipeSelect(id) {
    setSelectedRecipeId(id);
  }

  function handleRecipeAdd() {
    const newRecipe = {
      id: Date.now().toString(),
      name: "",
      servings: 1,
      cookTime: "",
      instructions: "",
      ingredients: [
        {
          id: Date.now().toString(),
          name: "",
          amount: "",
        },
      ],
    };
    setSelectedRecipeId(newRecipe.id);
    setRecipes([...recipes, newRecipe]);
  }

  function handleRecipeChange(id, recipe) {
    const newRecipes = [...recipes];
    const index = newRecipes.findIndex((r) => r.id === id);
    newRecipes[index] = recipe;
    setRecipes(newRecipes);
  }

  function handleRecipeDelete(id) {
    setRecipes(recipes.filter((recipe) => recipe.id !== id));
  }

  return (
    <>
      <div className="navbar">
        <div className="search-wrapper">
          <input type="text" onChange={(e) => handleSearch(e)} />
          {/* <button>Search</button> */}
        </div>
      </div>
      <RecipeContext.Provider value={recipeContextValue}>
        <RecipeList recipes={shownRecipes} />
        {/* <Recipe /> */}
        {selectedRecipe && <RecipeEdit recipe={selectedRecipe} />}
      </RecipeContext.Provider>
    </>
  );
}

export default App;
